/* ============================================
   ADVENTURE TIME ANIMATED ARTWORKS
   ============================================ */

/* Base Adventure Artwork Container */
.adventure-artwork {
  position: relative;
  width: 100%;
  height: 100%;
  min-height: 350px;
  overflow: hidden;
  border-radius: 12px;
  transition: transform 0.3s ease;
}

.project-card:hover .adventure-artwork {
  transform: scale\(1.02\);
}

/* ============================================
   ARTWORK 1: SKY ADVENTURE
   ============================================ */
.artwork-sky {
  background: linear-gradient\(180deg, 
    #87CEEB 0%,
    #B0E0E6 50%,
    #FFE5B4 100%\);
  animation: skyShift 10s ease-in-out infinite;
}

@keyframes skyShift {
  0%, 100% {
    background-position: 0% 0%;
  }
  50% {
    background-position: 100% 100%;
  }
}

[data-theme="dark"] .artwork-sky {
  background: linear-gradient\(180deg,
    #1a2a4e 0%,
    #2b4a7c 50%,
    #3d5a8c 100%\);
}

.artwork-cloud {
  position: absolute;
  font-size: 3rem;
  opacity: 0.8;
  animation: driftCloud 15s ease-in-out infinite;
}

.cloud-a {
  top: 20%;
  left: -10%;
  animation-delay: 0s;
}

.cloud-b {
  top: 50%;
  right: -10%;
  animation-delay: 5s;
  animation-duration: 18s;
}

@keyframes driftCloud {
  0%, 100% {
    transform: translateX\(0\) translateY\(0\);
  }
  50% {
    transform: translateX\(40px\) translateY\(-15px\);
  }
}

.artwork-sun {
  position: absolute;
  top: 15%;
  right: 15%;
  font-size: 4rem;
  animation: rotateSun 20s linear infinite;
}

@keyframes rotateSun {
  from {
    transform: rotate\(0deg\);
  }
  to {
    transform: rotate\(360deg\);
  }
}

.artwork-bird {
  position: absolute;
  top: 40%;
  left: 30%;
  font-size: 2rem;
  animation: flyBird 8s ease-in-out infinite;
}

@keyframes flyBird {
  0%, 100% {
    transform: translateX\(0\) translateY\(0\) rotate\(0deg\);
  }
  25% {
    transform: translateX\(60px\) translateY\(-20px\) rotate\(10deg\);
  }
  50% {
    transform: translateX\(120px\) translateY\(10px\) rotate\(-5deg\);
  }
  75% {
    transform: translateX\(60px\) translateY\(-10px\) rotate\(5deg\);
  }
}

/* ============================================
   ARTWORK 2: CANDY KINGDOM
   ============================================ */
.artwork-candy {
  background: linear-gradient\(135deg,
    #FFB6D9 0%,
    #FFC8DD 25%,
    #FFD4E5 50%,
    #FFAFCC 75%,
    #FFC0CB 100%\);
  background-size: 200% 200%;
  animation: candyShimmer 8s ease-in-out infinite;
}

@keyframes candyShimmer {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

[data-theme="dark"] .artwork-candy {
  background: linear-gradient\(135deg,
    #8B3A62 0%,
    #A0516D 25%,
    #B86B82 50%,
    #9D4866 75%,
    #825C78 100%\);
  background-size: 200% 200%;
}

.artwork-candy-piece {
  position: absolute;
  font-size: 3rem;
  animation: bobCandy 4s ease-in-out infinite;
}

.candy-1 {
  top: 15%;
  left: 20%;
  animation-delay: 0s;
}

.candy-2 {
  top: 45%;
  right: 25%;
  animation-delay: 1.5s;
}

.candy-3 {
  bottom: 20%;
  left: 30%;
  animation-delay: 3s;
}

@keyframes bobCandy {
  0%, 100% {
    transform: translateY\(0\) rotate\(0deg\) scale\(1\);
  }
  50% {
    transform: translateY\(-15px\) rotate\(5deg\) scale\(1.1\);
  }
}

.artwork-sparkle {
  position: absolute;
  font-size: 2rem;
  animation: sparkleShine 3s ease-in-out infinite;
}

.sparkle-1 {
  top: 30%;
  right: 15%;
  animation-delay: 0s;
}

.sparkle-2 {
  bottom: 30%;
  left: 15%;
  animation-delay: 1.5s;
}

@keyframes sparkleShine {
  0%, 100% {
    opacity: 0.3;
    transform: scale\(0.8\) rotate\(0deg\);
  }
  50% {
    opacity: 1;
    transform: scale\(1.3\) rotate\(180deg\);
  }
}

/* ============================================
   ARTWORK 3: GRASSLANDS
   ============================================ */
.artwork-grass {
  background: linear-gradient\(180deg,
    #87CEEB 0%,
    #98D8C8 30%,
    #8BC34A 60%,
    #7CB342 100%\);
  position: relative;
}

[data-theme="dark"] .artwork-grass {
  background: linear-gradient\(180deg,
    #1e3a2e 0%,
    #2b5f4f 30%,
    #3a7058 60%,
    #2d5a3f 100%\);
}

.artwork-grass::before {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 120px;
  background: var\(--color-grass-green\);
  border-radius: 50% 50% 0 0 / 20% 20% 0 0;
  animation: grassSway 6s ease-in-out infinite;
}

@keyframes grassSway {
  0%, 100% {
    transform: scaleY\(1\);
  }
  50% {
    transform: scaleY\(1.05\);
  }
}

.artwork-tree {
  position: absolute;
  bottom: 15%;
  left: 20%;
  font-size: 4rem;
  animation: treeRustle 5s ease-in-out infinite;
  z-index: 2;
}

@keyframes treeRustle {
  0%, 100% {
    transform: rotate\(0deg\);
  }
  50% {
    transform: rotate\(3deg\);
  }
}

.artwork-flower {
  position: absolute;
  bottom: 20%;
  font-size: 2rem;
  animation: flowerGrow 4s ease-in-out infinite;
  z-index: 2;
}

.flower-1 {
  left: 35%;
  animation-delay: 0s;
}

.flower-2 {
  right: 30%;
  animation-delay: 2s;
}

@keyframes flowerGrow {
  0%, 100% {
    transform: scale\(1\) rotate\(0deg\);
  }
  50% {
    transform: scale\(1.15\) rotate\(-5deg\);
  }
}

.artwork-butterfly {
  position: absolute;
  top: 35%;
  right: 20%;
  font-size: 2.5rem;
  animation: butterflyFly 10s ease-in-out infinite;
  z-index: 3;
}

@keyframes butterflyFly {
  0%, 100% {
    transform: translate\(0, 0\) rotate\(0deg\);
  }
  25% {
    transform: translate\(-40px, -30px\) rotate\(-10deg\);
  }
  50% {
    transform: translate\(-80px, 20px\) rotate\(10deg\);
  }
  75% {
    transform: translate\(-40px, 10px\) rotate\(-5deg\);
  }
}

/* ============================================
   ARTWORK 4: ICE KINGDOM
   ============================================ */
.artwork-ice {
  background: linear-gradient\(180deg,
    #E0F7FA 0%,
    #B3E5FC 30%,
    #81D4FA 60%,
    #4FC3F7 100%\);
  background-size: 200% 200%;
  animation: iceShimmer 10s ease-in-out infinite;
}

@keyframes iceShimmer {
  0%, 100% {
    background-position: 0% 0%;
    filter: brightness\(1\);
  }
  50% {
    background-position: 100% 100%;
    filter: brightness\(1.1\);
  }
}

[data-theme="dark"] .artwork-ice {
  background: linear-gradient\(180deg,
    #1a2942 0%,
    #2b4a7c 30%,
    #3d5a9c 60%,
    #4a6bb5 100%\);
  background-size: 200% 200%;
}

.artwork-snowflake {
  position: absolute;
  font-size: 2.5rem;
  animation: snowfall 8s linear infinite;
  opacity: 0.9;
}

.snow-1 {
  top: -10%;
  left: 20%;
  animation-delay: 0s;
  animation-duration: 8s;
}

.snow-2 {
  top: -10%;
  left: 50%;
  animation-delay: 3s;
  animation-duration: 10s;
}

.snow-3 {
  top: -10%;
  right: 20%;
  animation-delay: 5s;
  animation-duration: 9s;
}

@keyframes snowfall {
  0% {
    transform: translateY\(0\) rotate\(0deg\);
    opacity: 0;
  }
  10% {
    opacity: 0.9;
  }
  90% {
    opacity: 0.9;
  }
  100% {
    transform: translateY\(110vh\) rotate\(360deg\);
    opacity: 0;
  }
}

.artwork-star {
  position: absolute;
  top: 15%;
  right: 20%;
  font-size: 3rem;
  animation: starTwinkle 3s ease-in-out infinite;
}

@keyframes starTwinkle {
  0%, 100% {
    opacity: 0.6;
    transform: scale\(1\) rotate\(0deg\);
  }
  50% {
    opacity: 1;
    transform: scale\(1.2\) rotate\(90deg\);
  }
}

/* ============================================
   RESPONSIVE ADJUSTMENTS FOR ARTWORKS
   ============================================ */
@media \(max-width: 768px\) {
  .adventure-artwork {
    min-height: 280px;
  }

  .artwork-sun,
  .artwork-tree {
    font-size: 3rem;
  }

  .artwork-cloud,
  .artwork-candy-piece,
  .artwork-snowflake,
  .artwork-star {
    font-size: 2rem;
  }

  .artwork-bird,
  .artwork-butterfly,
  .artwork-flower,
  .artwork-sparkle {
    font-size: 1.5rem;
  }
}

@media \(max-width: 480px\) {
  .adventure-artwork {
    min-height: 220px;
  }

  .artwork-sun,
  .artwork-tree {
    font-size: 2.5rem;
  }

  .artwork-cloud,
  .artwork-candy-piece,
  .artwork-snowflake,
  .artwork-star {
    font-size: 1.8rem;
  }

  .artwork-bird,
  .artwork-butterfly,
  .artwork-flower,
  .artwork-sparkle {
    font-size: 1.2rem;
  }
}

/* Theme Toggle Emoji Style */
.theme-toggle-btn {
  font-size: 1.5rem;
}

.sun-emoji,
.moon-emoji {
  display: block;
  line-height: 1;
}

[data-theme="light"] .moon-emoji {
  display: none;
}

[data-theme="dark"] .sun-emoji {
  display: none;
}
